import React, { useState } from 'react';
import '../../src/weather.css';
import { WiDaySunny, WiRain, WiSnow, WiCloud, WiThunderstorm } from 'react-icons/wi';

function WeatherApp() {
    const [city, setCity] = useState("");
    const [weather, setWeather] = useState(null);
    const [error, setError] = useState(null);

    function GetWeather() {
        if (!city) {
            setError("Please enter a city name");
            setWeather(null);
            return;
        }
        setError(null);
        fetch(`https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=f0a7b3fccd6f021b78f97f7f8439c3da`)
            .then((result) => result.json())
            .then((response) => {
                if (response.cod !== 200) {
                    setError("City not found. Please try again.");
                    setWeather(null);
                } else {
                    setWeather(response);
                }
            });
    }

    function getWeatherIcon(weatherCondition) {
        switch (weatherCondition) {
            case 'Clear': return <WiDaySunny size={80} color="#f9d71c" />;
            case 'Rain': return <WiRain size={80} color="#007BFF" />;
            case 'Snow': return <WiSnow size={80} color="#00BFFF" />;
            case 'Clouds': return <WiCloud size={80} color="#A9A9A9" />;
            case 'Thunderstorm': return <WiThunderstorm size={80} color="#FFD700" />;
            default: return <WiCloud size={80} color="#A9A9A9" />;
        }
    }

    return (
        <div className="weather-app-container">
            <div className="weather-app-content">
                <h1 className="app-title">üå§Ô∏è Weather App</h1>
                <div className="input-container">
                    <input
                        type="text"
                        placeholder="Enter city name"
                        value={city}
                        onChange={(e) => setCity(e.target.value)}
                        className="city-input"
                    />
                    <button onClick={GetWeather} className="fetch-button">Get Weather</button>
                </div>

                {error && <p className="error-message">{error}</p>}

                {weather && (
                    <div className="weather-data fade-in">
                        <h3 className="city-name">{weather.name}</h3>
                        <div className="weather-icon">
                            {getWeatherIcon(weather.weather[0].main)}
                        </div>
                        <p className="weather-info"><strong>Temperature:</strong> {(weather.main.temp - 273.15).toFixed(2)} ¬∞C</p>
                        <p className="weather-info"><strong>Condition:</strong> {weather.weather[0].description}</p>
                        <p className="weather-info"><strong>Humidity:</strong> {weather.main.humidity}%</p>
                        <p className="weather-info"><strong>Wind Speed:</strong> {weather.wind.speed} m/s</p>
                    </div>
                )}
            </div>
        </div>
    );
}

export { WeatherApp };
