import React, { useState } from 'react';

function WeatherApp(){
    const[city,setCity]=useState("");
    const[weather,setweather]=useState(null);
    console.log(city)
    function GetWeather(){
        fetch(`https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=f0a7b3fccd6f021b78f97f7f8439c3da`).then((result)=>{
            return result.json().then((response)=>{
                console.log("Weather Fetch from api",response);
                setweather(response)
            })
        })
    }
    return(
        <div>
            <h1>Weather App</h1>
            <input type='text' value={city} onChange={(e)=>setCity(e.target.value)} />
            <button onClick={GetWeather}>Get Weather</button>

           {/* Weather Data Display */}
           {  (
                <div style={{ marginTop: '20px', padding: '10px', border: '1px solid #ddd' }}>
                    <h3>Weather in {weather.name}</h3>
                    <p><strong>Temperature:</strong> {(weather.main.temp - 273.15).toFixed(2)} Â°C</p>
                    <p><strong>Weather:</strong> {weather.weather[0].description}</p>
                    <p><strong>Humidity:</strong> {weather.main.humidity} %</p>
                    <p><strong>Wind Speed:</strong> {weather.wind.speed} m/s</p>
                </div>
            )}
        </div>
    )
}

export {WeatherApp};
