import React, { useState } from 'react';

function WeatherApp(){
    const[city,setCity]=useState("");
    const[Weather,setweather]=useState([]);
    console.log(city)
    console.log(Weather+"Weather Console")
    function GetWeather(){
        fetch(`https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=f0a7b3fccd6f021b78f97f7f8439c3da`).then((result)=>{
            return result.json().then((response)=>{
                console.log("Weather Fetch from api",response);
                setweather(response)
            })
        })
    }
    return(
        <div>
            <h1>Weather App</h1>
            <input type='text' value={city} onChange={(e)=>setCity(e.target.value)} />


            <button onClick={GetWeather}>Get Weather</button>
        </div>
    )
}

export {WeatherApp};
