import React, { useState } from "react";
import "../../src/weather.css";
import { WiDaySunny, WiRain, WiSnow, WiCloud, WiThunderstorm } from "react-icons/wi";

function WeatherApp() {
  const [city, setCity] = useState("");
  const [weather, setWeather] = useState(null);
  const [error, setError] = useState(null);

  function GetWeather() {
    if (!city) {
      setError("Please enter a city name");
      setWeather(null);
      return;
    }
    setError(null);
    fetch(
      `https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=f0a7b3fccd6f021b78f97f7f8439c3da`
    )
      .then((result) => result.json())
      .then((response) => {
        if (response.cod !== 200) {
          setError("City not found. Please try again.");
          setWeather(null);
        } else {
          setWeather(response);
        }
      });
  }

  function getWeatherIcon(weatherCondition) {
    switch (weatherCondition) {
      case "Clear":
        return <WiDaySunny size={100} color="#ffdf00" />;
      case "Rain":
        return <WiRain size={100} color="#007bff" />;
      case "Snow":
        return <WiSnow size={100} color="#a3e4ff" />;
      case "Clouds":
        return <WiCloud size={100} color="#cfcfcf" />;
      case "Thunderstorm":
        return <WiThunderstorm size={100} color="#ff8c00" />;
      default:
        return <WiCloud size={100} color="#d3d3d3" />;
    }
  }

  return (
    <div className="weather-app-container">
      <div className="content-card">
        <h1 className="app-title">Weather App ðŸŒˆ</h1>
        <div className="input-container">
          <input
            type="text"
            placeholder="Enter city name"
            value={city}
            onChange={(e) => setCity(e.target.value)}
            className="city-input"
          />
          <button onClick={GetWeather} className="fetch-button">
            Get Weather
          </button>
        </div>

        {error && <p className="error-message">{error}</p>}

        {weather && (
          <div className="weather-card">
            <div className="weather-icon">
              {getWeatherIcon(weather.weather[0].main)}
            </div>
            <h2>{weather.name}</h2>
            <p><strong>Temperature:</strong> {(weather.main.temp - 273.15).toFixed(2)} Â°C</p>
            <p><strong>Condition:</strong> {weather.weather[0].description}</p>
            <p><strong>Humidity:</strong> {weather.main.humidity}%</p>
            <p><strong>Wind Speed:</strong> {weather.wind.speed} m/s</p>
          </div>
        )}
      </div>
    </div>
  );
}

export { WeatherApp };
