import React, { useState } from 'react';
import '../../src/weather.css';
import { WiDaySunny, WiRain, WiSnow, WiCloud, WiThunderstorm } from 'react-icons/wi';

function WeatherApp() {
    const [city, setCity] = useState("");
    const [weather, setWeather] = useState(null);

    function GetWeather() {
        fetch(`https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=f0a7b3fccd6f021b78f97f7f8439c3da`)
            .then((result) => {
                return result.json().then((response) => {
                    console.log("Weather Fetch from API", response);
                    setWeather(response);
                });
            });
    }

    function getWeatherIcon(weatherCondition) {
        switch (weatherCondition) {
            case 'Clear': return <WiDaySunny size={50} color="#f9d71c" />;
            case 'Rain': return <WiRain size={50} color="#007BFF" />;
            case 'Snow': return <WiSnow size={50} color="#00BFFF" />;
            case 'Clouds': return <WiCloud size={50} color="#A9A9A9" />;
            case 'Thunderstorm': return <WiThunderstorm size={50} color="#FFD700" />;
            default: return <WiCloud size={50} color="#A9A9A9" />;
        }
    }

    return (
        <div className="weather-app-container">
            <div className="weather-app-content">
                <h1>Weather App</h1>
                <div className="input-container">
                    <input
                        type="text"
                        placeholder="Enter city name"
                        value={city}
                        onChange={(e) => setCity(e.target.value)}
                    />
                    <button onClick={GetWeather}>Get Weather</button>
                </div>

                {weather && (
                    <div className="weather-data">
                        <h3>{weather.name}</h3>
                        <div className="weather-icon">
                            {getWeatherIcon(weather.weather[0].main)}
                        </div>
                        <p><strong>Temperature:</strong> {(weather.main.temp - 273.15).toFixed(2)} Â°C</p>
                        <p><strong>Weather:</strong> {weather.weather[0].description}</p>
                        <p><strong>Humidity:</strong> {weather.main.humidity}%</p>
                        <p><strong>Wind Speed:</strong> {weather.wind.speed} m/s</p>
                    </div>
                )}
            </div>
        </div>
    );
}

export { WeatherApp };
